class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans= 0;
        unordered_map<int,int> prev;
        for(int i = 0; i < n;i++){
            unordered_map<int, int> curr;
            for (auto j : prev) {
                int and_value = j.first & nums[i];
                if (and_value == k) {
                    ans += j.second;
                }
                curr[and_value] += j.second;
            }
            if(nums[i] == k){
                ans++;
            }
            curr[nums[i]]++;
            prev = curr;
        }
        return ans;
    }
};
