class Solution {
    public long countSubarrays(int[] nums, int k) {
        long counter = 0;
        int n = nums.length;
        int[] streak = new int[30];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 30; j++) {
                if ((nums[i] & 1 << j) != 0) {
                    streak[j]++;
                } else {
                    streak[j] = 0;
                }
            }
            int minGood = i + 1;
            int maxBad = 0;
            for (int j = 0; j < 30; j++) {
                if ((k & 1 << j) != 0) {
                    minGood = Math.min(minGood, streak[j]);
                } else {
                    maxBad = Math.max(maxBad, streak[j]);
                }
            }
            counter += Math.max(minGood - maxBad, 0);
        }
        return counter;
    }
}
