int qym(int l, int r) {
    if (r<l) swap(l,r);
    int x = lg[r-l+1];
    int y = T[l][x] & T[r-(1<<x)+1][x];
    return y;
}
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans=0;
        int n = nums.size();
        vector<int> ps(n+5, 0), pre(n+5, 0);
        REQ(i,n) ps[i] = (nums[i-1]&k)==k ? 1 : 0;
        for(int i=n;i;i--) {
            if (ps[i]==1) pre[i]=max(i, pre[i+1]);
        }
        init(n, nums);
        for(int i=1,j=-1;i<=n;i++) {
            if (ps[i]==0) {
                continue;
            }
            if (j<i) j=i;
            while(j<=n and qym(i,j)>k) j++;
            if (j>n) break;
            if (pre[i] >= j and qym(i,j)==k) ans += pre[i]-(j) + 1;

        }
        return ans;
    }
};
