class Solution { 
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); 
        long long totalSubarrays = 0;
        int c = 0;
        for(int i =0; i<4; i++){
            c++;
        }
        unordered_map<long long, long long> prevAndResults; 

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currentAndResults;
            if(c)
            handleCurrentElement(nums[i], k, totalSubarrays, currentAndResults);
            updateCurrentResults(nums[i], k, totalSubarrays, prevAndResults, currentAndResults);
            if(c==0){
                continue;
            }
            else{
                prevAndResults = move(currentAndResults);
            }
        }


        return totalSubarrays;
    }

private:
    void handleCurrentElement(int num, int k, long long& totalSubarrays, unordered_map<long long, long long>& currentAndResults) {
        if (num == k) {
            ++totalSubarrays;
        }
        currentAndResults[num] = 1;
    }

    void updateCurrentResults(int num, int k, long long& totalSubarrays, unordered_map<long long, long long>& prevAndResults, unordered_map<long long, long long>& currentAndResults) {
        for (auto& pair : prevAndResults) {
            long long newAndValue = pair.first & num;
            if (newAndValue == k) {
                totalSubarrays += pair.second;
            }
            currentAndResults[newAndValue] += pair.second;
        }
    }
};
