class Solution {
public:
    long long countSubarrays(vector<int>& numbers, int target) {
        int size = numbers.size();
        long long totalCount = 0;
        map<long long, long long> previousCounts;

        for (int i = 0; i < size; ++i) {
            map<long long, long long> currentCounts;

            if (numbers[i] == target) {
                totalCount++;
            }

            currentCounts[numbers[i]] = 1;

            for (auto& entry : previousCounts) {
                long long bitwiseAndResult = entry.first & numbers[i];
                if (bitwiseAndResult == target) {
                    totalCount += entry.second;
                }
                currentCounts[bitwiseAndResult] += entry.second;
            }

            previousCounts = currentCounts;
        }

        return totalCount;
    }
};
