class Solution {
public:
    long long countSubarrays(vector<int>& a, int x) {
        int n = a.size();
        vector<int> res(n + 2, n), cut(n + 2, 0);
        for(int i = 0; i < 30; i++) {

            for(int j = 0; j < n; j++) {
                int cnt = 1 & (a[j] >> i), pvJ = j;
                while(cnt and j + 1 < n and (1 & (a[j + 1] >> i)) ) {
                    j++, cnt++;
                }

                for(int k = pvJ; k <= j; k++) {
                    if(1 & (x >> i)) {
                        res[k] = min(res[k], cnt);
                    }
                    else {
                        cut[k] = max(cut[k], cnt);
                    }
                    cnt--;
                }

            }
        }
        long long ans = 0;
        // cout << n << endl;
        for(int i = 0; i < n; i++) {
            // cout << res[i] << " " << cut[i] << "\n";
            res[i] = min(res[i], n - i);
            ans += max(0, res[i] - cut[i]);
        }
        return ans;
    }
};
