class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int s = nums.size();
        long long res = 0;
        unordered_map<long long, long long> map;

        for (int index = 0; index < s; index++) {
            unordered_map<long long, long long> temp;
            if (nums[index] == k) {
                res++;
            }
            temp[(long long) nums[index]] = 1LL;
            for (auto& entry : map) {
                long long nr = entry.first & nums[index];
                if (nr == k) {
                    res += entry.second;
                }
                temp[nr] += entry.second;
            }
            map = temp;
        }
        return res;

    }
};
