class Solution {
    #define ll long long
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll ct=0;
        map<ll,ll> mp;
        for(int i=0;i<n;i++)
        {
            map<ll,ll> mp1;
            if(nums[i]==k)
                ct++;
            mp1[nums[i]]=1;
            for(auto it:mp)
            {
                ll chk=it.first&nums[i];
                if(chk==k)
                    ct+=it.second;
                mp1[chk]+=it.second;
            }
            mp=mp1;
        }
        return ct;
    }
};
