class Solution {
 public:
  using LL = long long;
  long long countSubarrays(vector<int>& nums, int K) {
    unordered_map<int, int> mp;
    LL ret = 0;
    for (int x : nums) {
      unordered_map<int, int> rec;
      if (x == K) ret++;
      if (x >= K) rec[x]++;
      for (auto [k, v] : mp) {
        int y = k & x;
        if (y < K) continue;
        rec[y] += v;
        if (y == K) ret += v;
      }
      swap(rec, mp);
    }
    return ret;
  }
};
