class Solution {
public:
    long long countSubarrays(vector<int> &nums, int k) {
        long long ans = 0;
        unordered_map<long long,long long> m1;
        for (int i = 0; i < nums.size(); i++) {
            unordered_map<long long, long long> m2;
            m2[nums[i]] = 1;
            for (auto &it:m1) {
                if (k==(it.first & nums[i])) ans+=it.second;
                m2[(nums[i]&it.first)]+=it.second;
            }
            m1 = m2;
            if (k==nums[i]) ans++;
        }
        return ans;
    }
};
