class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll ans = 0;
        map<ll, ll> mp;
        for (auto x : nums){
            map<ll, ll> curr;
            curr[x] += 1;
            if (x == k) ans++;

            for (auto &y : mp){
                int res = y.ff&x;
                if (res == k) ans += y.ss;
                curr[res] += y.ss;
            }

            mp = curr;
        }

        return ans;
    }
};
