class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (nums[i] == k) {
                count++;
            }
            curr[nums[i]]++;
            for (auto& [val, freq] : mp) {
                long long temp = val & nums[i];
                if (temp == k) count += freq;
                curr[temp] += freq;
            }
            mp = curr;
        }

        return count;  
    }
};
