#define ll long long
const int M = 1e9 + 7;
const int MAX_N = 500000;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll res = 0, n = nums.size();
        unordered_map<int, ll> prev;
        for(auto& num : nums) {
            res += (num == k);
            unordered_map<int, ll> cur; 
            cur[num] = 1;
            for (auto& [x, y] : prev) {
                cur[x & num] += y;
                if ((x & num) == k) {
                    res += y;
                }
            }
            prev = cur;
        }
        return res;
    }
};
