using ll = long long;
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        ll n = arr.size(), cnt = 0;

        map<ll, ll> _p, _c; 
        for (int i = 0; i < n; ++i) {
            _c = map<ll, ll> ();
            if (arr[i] == k) cnt++;
            _c[arr[i]] = 1;

            for (auto& [v, fr] : _p) {
                ll n = v & arr[i];
                if (n == k)
                    cnt += fr;
                _c[n] += fr;
            }

            _p = _c;
        }

        return cnt;
    }
};
