class Solution {
    public long countSubarrays(int[] nums, int k) {
        long ans = 0;
        Map<Integer, Integer> map = new HashMap<>();
        Map<Integer, Integer> curr;

        for (int el : nums) { // calculate for all possible subarrays ending at el
            curr = new HashMap<>();
            curr.put(el, 1);
            if (el == k) ans++;

            for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
                int and = el & entry.getKey();
                if (and == k) {
                    ans += entry.getValue();
                }
                curr.put(and, curr.getOrDefault(and, 0) + entry.getValue());
            }

            map = curr;
        }

        return ans;
    }
}
