class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;

        map<int, int> endingAtPrev;

        for(int i = 0; i < n; i++){
            map<int, int> endingAtCurr;
            endingAtCurr[nums[i]] = 1;
            if (nums[i] == k) ans++;
            for(auto& it : endingAtPrev){
                int temp = it.first & nums[i];
                if (temp == k) ans += it.second;
                endingAtCurr[temp] += it.second;                
            }
            endingAtPrev = endingAtCurr;
        }

        return ans;

    }
};
