#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll len=nums.size(),cntSub = 0;
        map<ll,ll>prev;
        int i=0;
        while(i<len) {
            map<ll,ll>res;
            if(nums[i]==k) {
                cntSub++;
            }
            res[nums[i]]=1;
            for (auto it:prev) {
                ll var = it.first & nums[i];
                res[var] += it.second;
                if (var == k) {
                    cntSub +=it.second;
                }
            }
            prev=res;
            i++;
        }
        return cntSub;

    }
};
