class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll count = 0;
        unordered_map<ll, ll> freq;

        for (int i = 0; i < n; ++i) {
            unordered_map<ll, ll> newFreq;
            if (nums[i] == k) {
                count++;
            }
            newFreq[nums[i]]++;

            for (auto& [value, freqCount] : freq) {
                ll newValue = value & nums[i];
                if (newValue == k) {
                    count += freqCount;
                }
                newFreq[newValue] += freqCount;
            }

            freq = newFreq;
        }

        return count;
    }
};
