class Solution {
public:
    long long ans=0;
    int n=0;
    map<long long,long long>pr;
    long long countSubarrays(vector<int>& nums, int k) {
        n=nums.size();
        for(int i=0;i<n;i++){
            if(nums[i]==k) ans++;
            map<long long,long long>cr;
            cr[nums[i]]=1;
            for(auto it:pr){
                auto v=it.first;
                auto f=it.second;
                long long c=v&nums[i];
                if(c==k) ans+=f;
                cr[c]+=f;
            }
            pr=cr;
        }
        return ans;
    }
};
