class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        int[][] arr = new int[31][n];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 31; j++) {
                arr[j][i] = (nums[i] >> j) & 1;
            }
        }
        int[][] arr2 = new int[31][n];
        for (int i = 0; i < 31; i++) {
            int cur = -1;
            for (int j = 0; j < n; j++) {
                if (arr[i][j] == 0) {
                    cur = j;
                }
                arr2[i][j] = cur;
            }
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            int l = 0;
            int r = i;
            for (int j = 0; j < 31; j++) {
                int t = (k >> j) & 1;
                if (t == 1) {
                    l = Math.max(l, arr2[j][i] + 1);
                } else {
                    r = Math.min(r, arr2[j][i]);
                }
            }
            ans += Math.max(0, r - l + 1);
        }
        return ans;
    }
}
