class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,int> prev;
        long long ans = 0;
        for(auto it: nums) {
            map<int,int> curr;
            for(auto b: prev) {
                curr[b.first&it] += b.second; 
            }
            curr[it]++;
            ans += curr[k];
            prev = curr;
        }
        return ans;
    }
};
