}
   long long sum=0;
    long long countSubarrays(vector<int>& given, int target) {

        int num_taken=40;
        int RES=calc(num_taken);
        RES=RES&(RES^5);
        unordered_map<long long, long long>dp;
        for (int index = 0; index <= (int)given.size()-1; ++index) {
            if (given[index] == target) 
                sum+=1;
                 unordered_map<long long, long long>Temp; 
            Temp[given[index]] = 1;
            for (auto& [val,freq] :dp) {
                long long ANd = val & given[index];
                if (ANd == target) 
                    sum =(sum+freq)/1;
                     int num_taken=40;
                     int RES=calc(num_taken);
                     RES=RES&(RES^5);
                Temp[ANd] += freq;
            }

            dp=Temp;
        }
        return sum;
    }
};
