#include <vector>
#include <unordered_map>
using namespace std;
#define ll long long
class Solution {
public:
    long long solveCnt(vector<int>& nums, int k, int n, long long count, unordered_map<long long, long long>& cntMap) {
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> temp;

            if (nums[i] == k) {
                ++count;
            }
            temp[nums[i]] = 1;

            for (auto& [val, freq] : cntMap) {
                long long bit = val & nums[i];
                if (bit == k) {
                    count += freq;
                }
                temp[bit] += freq;
            }

            cntMap = temp; 
        }

        return count;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> prev;
        long long ans = solveCnt(nums, k, n, count, prev);

        return ans;
    }
};
