class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        long long ans = 0;
        m[nums.back()] = 1;
        ans += m[k];
        int n = nums.size();
        for (int i = n - 2; i >= 0; i--) {
            unordered_map<int,int> temp;
            temp[nums[i]]++;
            for (auto& [x, y] : m) {
                temp[nums[i] & x] += y;
            }
            swap(temp, m);
            // cout << "## " << m[1] << endl;
            ans += m[k];
        }
        return ans;
    }
};
