class Solution {
    public long countSubarrays(int[] nums, int k) {
        Map<Long, Long> mp = new HashMap<>();
        long ans = 0;

        for (int i = 0; i < nums.length; i++) {
            Map<Long, Long> cur = new HashMap<>();
            if (nums[i] == k) {
                ans++;
            }
            cur.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : mp.entrySet()) {
                long freq = entry.getValue();
                long val = entry.getKey();
                long nxt = val & nums[i];

                if (nxt == k) {
                    ans = ans + freq;
                }

                cur.put(nxt, cur.getOrDefault(nxt, 0L) + freq);
            }

            mp = cur;
        }

        return ans;

    }
}
