class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<int,long long>mp;
        for(int i=0;i<n;i++){
            unordered_map<int,long long> curr;
            if (nums[i]==k){
                ans++;
            }
            // Update the curr
            curr[nums[i]] = 1;
            for(auto it:mp){
                int res = (it.first & nums[i]);
                if (res==k){
                    ans+=it.second;
                }
                curr[res]+=it.second;
            }
            // Change original to curr
            mp = curr;
        }
        return ans;
    }
};
