class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0 ;
        vector<pair<int,int>> u,v;

        for(int i=0;i<nums.size();++i){
            for(auto &it: u)it.first &= nums[i];
            u.emplace_back(nums[i],i);
            v.clear();
            for(auto &it: u)if(v.empty() || v.back().first!=it.first)v.push_back(it);
            const int sz = v.size();
            for(int j=0;j<sz;++j)if(v[j].first == k){
                if(j==sz-1) ans += i-v[j].second+1;
                else ans += v[j+1].second - v[j].second;
            }
            u.swap(v);
        }
        return ans;
    }
};
