class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        result = 0
        prefix = {}

        for num in nums:
            result += num == k
            new_prefix = {num: 1}
            for prev_num, cnt in prefix.items():
                new_val = prev_num & num
                result += (new_val == k) * cnt
                new_prefix[new_val] = new_prefix.get(new_val, 0) + cnt

            prefix = new_prefix

        return result
