class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<long long,long long>p;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>c;
            if(nums[i]==k)cnt++;
            c[nums[i]]=1;
            for(auto &[val,freq]:p){
                long long ar=val&nums[i];
                if(ar==k)cnt+=freq;
                c[ar]+=freq;
            }
            p=c;
        }
        return cnt;

    }
};
