from collections import defaultdict
from typing import List

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        n,current_and =len(nums), defaultdict(int)
        x=set()
        a=1
        for i in range(n):
            new_current_and = defaultdict(int)
            for values in current_and:
                new_and_value = values & nums[i]
                a+=1
                new_current_and[new_and_value] += current_and[values]

            new_current_and[nums[i]] += 1
            x.add(i)
            if not x:
                return 0
            if k in new_current_and:
                count += new_current_and[k]

            current_and = new_current_and

        return count*(a//a)
