class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> previousResults;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currrentResults; 

            if (nums[i] == k) {
                ++count;
            }
            currrentResults[nums[i]] = 1;

            for (auto& [val, freq] : previousResults) {
                long long newResult = val & nums[i];
                if (newResult == k) {
                    count += freq;
                }
                currrentResults[newResult] += freq;
            }

            previousResults = currrentResults;
        }

        return count;
    }
};
