class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long count=0;
        unordered_map<long long,long long>mp;
        for(int i=0;i<n;i++){
        unordered_map<long long,long long>mp2;
        if(nums[i]==k){
            ++count;

        }
        mp2[nums[i]]=1;
        for(auto &[a,b]:mp){
            long long newres=a&nums[i];
            if(newres==k){
                count+=b;
            }
            mp2[newres]+=b;
        }
        mp=mp2;
        }
        return count;
    }
};
