N = 30
X = 0x3fff_ffff

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        i = ans = 0
        n = len(nums)
        cnter = [0] * N

        while i < n:
            if nums[i] & k != k:
                i += 1
                continue

            b = i
            i += 1

            while i < n and nums[i] & k == k:
                i += 1

            cur = X
            r = b

            for p in range(N):
                cnter[p] = 0

            for l in range(b, i):

                while r < i and cur > k:
                    cur &= nums[r]
                    for p in range(N):
                        cnter[p] += nums[r] >> p & 1 ^ 1
                    r += 1

                if cur == k:
                    ans += i - r + 1

                for p in range(N):
                    if nums[l] >> p & 1 == 0:
                        cnter[p] -= 1
                        if cnter[p] == 0:
                            cur |= 1 << p

        return ans
