#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
         ll cnt = 0;
        unordered_map<ll,ll> mp ;
        for(int i = 0 ; i < a.size() ; i++){
             unordered_map<ll,ll> next;
             if(a[i] == k)cnt++ ;
             next[a[i]] = 1LL ;
            for(auto j: mp){
                ll x = j.first ;
                if((x & a[i]) == k)cnt += j.second;
                next[(x & a[i])] += j.second ;
            }
            mp = next ;
        }
        return cnt ;
    }
};
