class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        unordered_map<long long,long long>mp;

        for(int i=0;i<n;i++){
            unordered_map<long long,long long>curr;

            if(nums[i] ==k){
                ++ans;
            }
            curr[nums[i]] = 1;

            for(auto& [x,y] :mp){
                long long ne = x&nums[i];
                if(ne == k){
                    ans+=y;
                }
                curr[ne] += y;
            }
            mp = curr;
        }

        return ans;
    }
};
