class Solution {
public:
    #define ll long long 
    long long countSubarrays(vector<int>& nums, int k) {
        vector<ll> aa(32,0),bb(32,1);
        ll ans=0,n=nums.size();
        for(int i=0;i<n;i++){
            for(int j=0;j<32;j++){
                if((nums[i]&(1ll<<j))==0){
                    aa[j]=i+1;
                    bb[j]=i+2;
                }
            }
            ll pos1=-1,pos2=i+2;
            for(int j=0;j<32;j++){
                if((k&(1ll<<j))==0){
                    pos2=min(pos2,aa[j]);
                }else{
                    pos1=max(pos1,bb[j]);
                }
            }
            // cout<<i<<" "<<pos1<<" "<<pos2<<endl;
            if(pos1==-1){
                ans+=pos2;
            }else if(pos2==i+2){
                ans+=(i+1-pos1+1);
            }else if(pos1<=pos2){
                ans+=(pos2-pos1+1);
            }
        }
        return ans;
    }
};
