#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        int n = v.size();
        ll int ans = 0;
        unordered_map<ll int, ll int> mp;
        for(int i=0;i<n;i++){
            unordered_map<ll int,ll int> nmap;
            ans += (v[i] == k);
            nmap[v[i]]++;
            for(auto x:mp){
                ll int cur = (1ll*x.first)&v[i];
                if(cur == k){
                    ans += x.second;
                }
                nmap[cur] += x.second;
            }
            mp = nmap;
        }
        return ans;
    }
};
