class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size(); 
        long long sum = 0;
        unordered_map<long long, long long> prev;

        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> curr;
            long long tar = k;
            if (nums[i] == tar) {
                sum++;
            }
            curr[nums[i]] = 1;
            for (auto& [a, b] : prev) {
                long long newAnd = a & nums[i]; 
                if (newAnd == tar) {
                    sum += b;
                }
                curr[newAnd] += b;
            }

            prev = curr;
        }

        return sum;

    }
};
