class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

         unordered_map<long long, long long> mp;
         long long ct=0;

         for(int i=0; i<nums.size(); i++){
            unordered_map<long long, long long> mp1;

            mp1[nums[i]]++;
            if(nums[i]==k) ct++;

            for(auto it: mp){
                int andval=(it.first&nums[i]);
                if(andval==k) ct+=it.second;


                mp1[andval]+=it.second;
            }

            mp=mp1;



         }

         return ct;
    }
};
