class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> mp1;
        for (int i = 0; i < n; ++i) {
        unordered_map<int, int> mp2;
        for (auto& [andval, cnt] : mp1) {
            int andval2 = andval & nums[i];
            mp2[andval2] += cnt;
        }
        mp2[nums[i]]++;
        mp1 = mp2;
        if (mp1.find(k) != mp1.end()) {
            count += mp1[k];
        }
    }

    return count;
    }
};
