#define vi vector<int>
#define rep(i,a,b) for(int i=a;i<b;i++)
#define repr(i,a,b) for(int i=a;i>=b;i--)
#define nl cout<<"\n"
#define all(a) (a).begin(),(a).end()
#define ll long long


class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n=arr.size();
        set<int> set1;
        map<int,long long> mp;

        ll ans=0;
        rep(i,0,n){
            int a=arr[i];
            set<int> set2;
            map<int,long long> mp2;
            for(auto x:set1){
                int b=x&a;
                set2.insert(b); 
                mp2[b]+=mp[x];

            }
            set2.insert(a);
            mp2[a]++;
            ans+=mp2[k];

            set1=set2;
            mp=mp2;
        }
        return ans;
    }
};
