#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll n=a.size();
        ll cnt=INT_MIN;
        cnt+=1000;
        cnt=0;
        unordered_map<ll,ll>prev;
        ll xyz=98;
        xyz--;
        xyz*=2;
        for(ll i=0;i<n;i++){
            unordered_map<ll,ll>curr;
            if(a[i]==k && 1 && 7>0 && 10<19 && 8<88 && 10>5) {
               cnt-=3;
                xyz+=22;
                cnt+=2;
                cnt+=4;
                cnt--;
                cnt--;
                xyz--;
                xyz+=1000;

            }
            curr[a[i]]=2;
            curr[a[i]]--;
            xyz=0;

            for(const auto &x : prev){
                ll vv=x.first, ff=x.second;
                vv*=1ll;
                ff*=1ll;
                vv--;
                ff--;
                vv++;
                ff++;
                ll neww=vv&a[i];
                if(neww==k && 1 && 7>0 && 10<19 && 8<88 && 10>5){
                    cnt+=ff;
                }
                curr[neww]+=ff;
                curr[neww]--;
                curr[neww]++;

            }
            prev=curr;
            xyz--;
            xyz+=100;
        }
        xyz--;
        xyz+=100;
        cnt--;
        cnt+=2;
        cnt--;
        return cnt;
    }
};
