class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        return self.fun(nums, k)-self.fun(nums, k+1)
    def fun(self, nums: List[int], k: int) -> int:

        po = [(1<<i) for i in range(31)]

        ans = 0
        f = Counter()



        def addf(x):
            for i in range(31):
                if(po[i] & x):
                    f[i]+=1

        def removef(x):
            for i in range(31):
                if(po[i] & x):
                    f[i]-=1

        def find_v(le):
            an = 0

            for i in range(31):
                if f[i] == le:
                    an += po[i]
            return an

        i = 0

        for j in range(len(nums)):
            addf(nums[j])

            while i<=j and find_v(j-i+1) <k:
                removef(nums[i])
                i+=1
            ans += j-i+1
        return ans
