func countSubarrays(nums []int, k int) int64 {
    ans := 0
    set := map[int]int{}
    for i := 0; i < len(nums); i++ {
        tmp := map[int]int{}
        for and, cnt:= range set {
            if and & nums[i] < k {
                continue 
            }
            tmp[and & nums[i]] += cnt
        }
        if nums[i] >= k {
            tmp[nums[i]]+=1
        }
        ans += tmp[k]
        set = tmp 
    }
    return int64(ans)
}
