class Solution {
public:
    long long int call(vector<int>&nums,int &k){
        int n = nums.size();
        long long count = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> mp1; 

            if (nums[i] == k) {
                ++count;
            }
            mp1[nums[i]] = 1;

            for (auto& [val, freq] : mp) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
                mp1[newAndResult] += freq;
            }

            mp = mp1;
        }

        return count;

    }
    long long countSubarrays(vector<int>& nums, int k) {

        return call(nums,k);
    }
};
