from typing import List
from collections import defaultdict

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        n = len(nums)
        and_map = defaultdict(int)

        for num in nums:
            new_map = defaultdict(int)
            if num == k:
                count += 1
            new_map[num] += 1

            for key in and_map:
                new_and = key & num
                if new_and == k:
                    count += and_map[key]
                new_map[new_and] += and_map[key]

            and_map = new_map

        return count
