from typing import List

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        ans = 0
        n = len(nums)
        i0 = -1
        v = []

        for i in range(n):
            v = [(p[0] & nums[i], p[1]) for p in v]

            nv = []
            for p in v:
                x, y = p[0], p[1]
                if not nv or x != nv[-1][0]:
                    nv.append(p)

            nv.append((nums[i], i))

            if nums[i] == k:
                ans += 1

            for j in range(len(nv) - 1):
                w, x, y = nv[j][0], nv[j][1], nv[j+1][1] - 1
                l = i - y + 1
                r = i - x + 1
                if w == k:
                    ans += r - l + 1

            v = nv

            while len(v) > 1 and v[-1][0] == v[-2][0]:
                v.pop()

        return ans
