class Solution {
public:
    void fun(map<long long, long long> &pr,vector<int>& v,int &k,long long &c,map<long long, long long> &cr,int &i){
        for (auto& [z, f] : pr) {
                long long x = z & v[i];
                if (x == k) {
                    c += f;
                }
                cr[x] += f;
            }
    }
    long long countSubarrays(vector<int>& v, int k) {
        long long c = 0;
        int n = v.size();
        map<long long, long long> pr;
        for (int i = 0; i < n; ++i) {
            map<long long, long long> cr; 
            if (v[i] == k) {
                ++c;
            }
            cr[v[i]] = 1;
            fun(pr,v,k,c,cr,i);


            pr = cr;
        }
        return c;
    }
};
