class Solution:
    def countSubarrays(self, nums: List[int], tar: int) -> int:
        n = len(nums)
        ans = [0] * n
        ors = []
        res = 0
        for i in range(n - 1, -1, -1):
            num = nums[i]

            ors.append([-1, 1])
            k = 0
            for p in ors:
                p[0] &= num

            for j in range(len(ors)):
                if ors[k][0] == ors[j][0]:
                    if j != k:
                        ors[k][1] += ors[j][1]
                else:
                    k += 1
                    ors[k] = ors[j] 
            del ors[k + 1:]
            # print(ors)
            for s in ors:
                if s[0] == tar:
                    res += s[1]
        return res
