#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll v=0, n=nums.size();
        map<ll,ll> mp;
        for(int it:nums){
            if(it==k) v++;
            map<ll,ll> mpt;
            mpt[it]=1;
            for(auto it2:mp){
                ll temp= it & it2.first;
                if(temp==k) v += it2.second;
                mpt[temp]+= it2.second;
            }
            mp=mpt;
        }

        return v;


    }
};
